import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, Alert, Image} from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';

export default function Dashboard({ navigation }) {

  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('Perimiter')}>
        <Text style={styles.buttonText}>Perimiter</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('MyMaps')}>
        <Text style={styles.buttonText}>MyMaps</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.button} onPress={() => navigation.navigate('MyLocation')}>
        <Text style={styles.buttonText}>MyLocation</Text>
      </TouchableOpacity>
    </View>
  )
}

const styles = StyleSheet.create({
  button: {
    width: 250,
    padding: 5,
    backgroundColor: '#0E2D6D',
    borderWidth: 2,
    borderColor: 'white',
    borderRadius: 15,
    alignSelf: 'center',
    margin: "2%",
  },
  buttonText: {
    fontSize:20,
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  container: {
    height: '100%',
    width: '100%',
    backgroundColor: '#FFFFFF',
    alignItems: 'center',
    justifyContent: 'center',
  },
  text: {
    textAlign: 'center',
    fontSize: 20,
    fontStyle: 'italic',
    marginTop: '2%',
    marginBottom: '10%',
    fontWeight: 'bold',
    color: 'black',
  },
  titleText: {
    textAlign: 'center',
    fontSize: 30,
    fontWeight: 'bold',
    color: '#2E6194',
  },
  titleContainer: {
    backgroundColor: '#FFFFFF',
    alignItems: 'center', 
    justifyContent: 'center',
    position: 'absolute',
    top: 30,
  },
  title: {
    fontSize: 15,
    fontWeight: 'bold',
    color: 'black',
    textAlign: 'center'
  },
});
