import React, { useState, useEffect } from 'react';
import { FlatList, Modal, StyleSheet, Text, TextInput, View, Pressable, Dimensions} from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { DataStore } from 'aws-amplify';
import { Location } from './models';
//import MapView, { PROVIDER_GOOGLE } from 'react-native-maps';
//import * as defaultLocation from 'expo-location';


const Header = () => (
  <View style={styles.headerContainer}>
    <Text style={styles.headerTitle}>My Location List</Text>
  </View>
);


const AddLocationModal = ({ modalVisible, setModalVisible }) => {
  const [location1, setLocation1] = useState('');
  const [location2, setLocation2] = useState('');
  const [location3, setLocation3] = useState('');
  const [location4, setLocation4] = useState('');

  async function addLocation() {
    await DataStore.save(new Location({ location1, location2, location3, location4, isComplete: false }));
    setModalVisible(false);
    setLocation1('');
    setLocation2('');
    setLocation3('');
    setLocation4('');
  }

  function closeModal() {
    setModalVisible(false);
  }

  return (
    <Modal
      animationType="fade"
      onRequestClose={closeModal}
      transparent
      visible={modalVisible}
    >
      <View style={styles.modalContainer}>
        <View style={styles.modalInnerContainer}>
          <Pressable onPress={closeModal} style={styles.modalDismissButton}>
            <Text style={styles.modalDismissText}>X</Text>
          </Pressable>
          <TextInput
            onChangeText={setLocation1}
            placeholder="Location1"
            style={styles.modalInput}
          />
          <TextInput
            onChangeText={setLocation2}
            placeholder="Location2"
            style={styles.modalInput}
          />
            <TextInput
            onChangeText={setLocation3}
            placeholder="Location3"
            style={styles.modalInput}
          />
            <TextInput
            onChangeText={setLocation4}
            placeholder="Location4"
            style={styles.modalInput}
          />
          <Pressable onPress={addLocation} style={styles.buttonContainer}>
            <Text style={styles.buttonText}>Save Location</Text>
          </Pressable>
        </View>
      </View>
    </Modal>
  );
};

const LocationList = () => {
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    //query the initial todolist and subscribe to data updates
    const subscription = DataStore.observeQuery(Location).subscribe((snapshot) => {
      //isSynced can be used to show a loading spinner when the list is being loaded. 
      const { items, isSynced } = snapshot;
      setLocations(items);
    });
    //unsubscribe to data updates when component is destroyed so that we don’t introduce a memory leak.
    return function cleanup() {
      subscription.unsubscribe();
    }
  }, []);

  async function deleteLocation(item) {
    try {
        await DataStore.delete(item);
      } catch (e) {
        console.log('Delete failed: $e');
      }
  }

  async function updateLocation1(updateValue, location) {
    //update the location item with updateValue
    await DataStore.save(
     Location.copyOf(location, updated => {
       updated.location1 = 'boom1'
     })
    ); 
  }

  async function clearLocation1(updateValue, location) {
    //update the location item with updateValue
    await DataStore.save(
     Location.copyOf(location, updated => {
       updated.location1 = ' '
     })
    ); 
  }

  async function updateLocation2(updateValue, location) {
    //update the location item with updateValue
    await DataStore.save(
     Location.copyOf(location, updated => {
       updated.location2 = 'boom2'
     })
    ); 
  }

  
async function clearLocation2(updateValue, location) {
  //update the location item with updateValue
  await DataStore.save(
   Location.copyOf(location, updated => {
     updated.location2 = ' '
   })
  ); 
}

async function updateLocation3(updateValue, location) {
  //update the location item with updateValue
  await DataStore.save(
   Location.copyOf(location, updated => {
     updated.location3 = 'boom3'
   })
  ); 
}

async function clearLocation3(updateValue, location) {
  //update the location item with updateValue
  await DataStore.save(
   Location.copyOf(location, updated => {
     updated.location3 = ' '
   })
  ); 
}

async function updateLocation4(updateValue, location) {
  //update the location item with updateValue
  await DataStore.save(
   Location.copyOf(location, updated => {
     updated.location4 = 'boom4'
   })
  ); 
}

async function clearLocation4(updateValue, location) {
  //update the location item with updateValue
  await DataStore.save(
   Location.copyOf(location, updated => {
     updated.location4 = ' '
   })
  ); 
}

  const renderItem = ({ item }) => (
    <View style={styles.container}>
      <Text style={styles.infoBox} >Location1: {`${item.location1}`}</Text>
       <View style={styles.doublebutton}>
      <TouchableOpacity style={styles.halfbutton} onPress={() => {updateLocation1(item.location1, item);}}>
        <Text style={styles.halfbuttonText}>update</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.halfbutton} onPress={() => {clearLocation1(item.location1, item);}}>
        <Text style={styles.halfbuttonText}>clear</Text>
      </TouchableOpacity>
      </View>

      <Text style={styles.infoBox} >Location2: {`${item.location2}`}</Text>
       <View style={styles.doublebutton}>
      <TouchableOpacity style={styles.halfbutton} onPress={() => {updateLocation2(item.location2, item);}}>
        <Text style={styles.halfbuttonText}>update</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.halfbutton} onPress={() => {clearLocation2(item.location1, item);}}>
        <Text style={styles.halfbuttonText}>clear</Text>
      </TouchableOpacity>
      </View>

      <Text style={styles.infoBox} >Location3: {`${item.location3}`}</Text>
       <View style={styles.doublebutton}>
      <TouchableOpacity style={styles.halfbutton} onPress={() => {updateLocation3(item.location3, item);}}>
        <Text style={styles.halfbuttonText}>update</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.halfbutton} onPress={() => {clearLocation3(item.location1, item);}}>
        <Text style={styles.halfbuttonText}>clear</Text>
      </TouchableOpacity>
      </View>

      <Text style={styles.infoBox} >Location4: {`${item.location4}`}</Text>
       <View style={styles.doublebutton}>
      <TouchableOpacity style={styles.halfbutton} onPress={() => {updateLocation4(item.location4, item);}}>
        <Text style={styles.halfbuttonText}>update</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.halfbutton} onPress={() => {clearLocation4(item.location4, item);}}>
        <Text style={styles.halfbuttonText}>clear</Text>
      </TouchableOpacity>
      </View>
    </View>
  );

  // async function getCurrentLocation() {
  //   const [mapLocation, setMapLocation] = useState(null);
  //   const [errorMsg, setErrorMsg] = useState(null);
  
  //   useEffect(() => {
  //     (async () => {
  //       let { status } = await defaultLocation.requestForegroundPermissionsAsync();
  //       if (status !== 'granted') {
  //         setErrorMsg('Permission to access location was denied');
  //         return;
  //       }
  //       let mapLocation = await defaultLocation.getCurrentPositionAsync({
  //         accuracy: defaultLocation.Accuracy.Balanced,
  //         enableHighAccuracy: true,
  //         timeInterval: 5
  //       });
  //       setMapLocation(mapLocation);
  //       }
  //     )();
  //     }, 
  //     []
  //   );
  
  //   let text1 = 'Waiting..';
  //   let text2 = 'Waiting..';
  //   if (errorMsg) {
  //     text1 = errorMsg;
  //     text2 = errorMsg;
  //   } else if (mapLocation) {
  //     text1 = JSON.stringify(mapLocation.coords.latitude);
  //     text2 = JSON.stringify(mapLocation.coords.longitude);
  //   }
  
  //   const sendLoc =() =>{
  //     console.log("Use these variables to send current location(",text1, ",",text2,")")
  //   }
  //   return (
  //     <View style={styles.container} >
  //       <Text style={styles.boldText}>You are Here{text1}</Text>
  //       <Text style={styles.boldText}>You are Here{text2}</Text>
  //     </View>
  //   )
  // }

  return (
    <FlatList
      data={locations}
      keyExtractor={({ id }) => id}
      renderItem={renderItem}
    />
  );
};


export default function Events ({ navigation }) {

  const [modalVisible, setModalVisible] = useState(false);

  const handlePress = () => {
    navigation.replace('Dashboard');
  };

  return (
    <View style={styles.container} >
      <Header />
      <LocationList />
        <TouchableOpacity style={styles.button} onPress={handlePress}>
            <Text style={styles.buttonText}>Back</Text>
        </TouchableOpacity>
    </View>
  );
};

