import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Dimensions } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import * as Location from 'expo-location';
import MapView, { Marker } from 'react-native-maps';
import { FontAwesome } from '@expo/vector-icons';
import { Constants, Permissions } from 'expo';

//let Marker = MapView.Marker;

export default function Perimiter({navigation}) {
    const [location, setLocation] = useState(null);
    const [errorMsg, setErrorMsg] = useState(null);

    const handlePress = () => {
      navigation.replace('Dashboard');
    };
    

 

    useEffect(() => {
      (async () => {
        let { status } = await Location.requestForegroundPermissionsAsync();
        if (status !== 'granted') {
          setErrorMsg('Permission to access location was denied');
          return;
        }

        let location = await Location.getCurrentPositionAsync({});
        setLocation(location);
        
        let { coords } = await Location.getCurrentPositionAsync();
        
        if (coords) {
          const { latitude, longitude } = coords;
          let response = await Location.reverseGeocodeAsync({
            latitude,
            longitude
          })
        };

        for (let item of response) {
          let address = `${item.name}, ${item.street}, ${item.postalCode}, ${item.city}`;
          setDisplayCurrentAddress(address);
        }

      })();
    }, []);
  
    let text = 'Waiting..';
    if (errorMsg) {
      text = errorMsg;
    } else if (location) {
      text = JSON.stringify(location);
      console.log(location.coord);
    }

    return (
      <View style={styles.container}>
        <Text style={styles.title}>Perimiter</Text>
        <Text style={styles.boldText}>You are Here{text}</Text>
        
        {/* <MapView style={styles.map}>
                {location ? (
                    <Marker coordinate={location} title="My location" >
                        <FontAwesome name="map-marker" size={40} color="#B12A5B" />
                    </Marker>
                ):
                    <Text>{errorMsg}</Text>
                }
            </MapView> */}
        <TouchableOpacity style={styles.button} onPress={handlePress}>
          <Text style={styles.buttonText}>Back</Text>
        </TouchableOpacity>
      </View>
      
    );
    }

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 50,
    padding: 16,
  },
  boldText: {
    fontSize: 30,
    color: 'red',
  },
  map: {
    width: Dimensions.get('screen').width,
    height: Dimensions.get('screen').height * 0.90,
  }, 
});
