import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Dimensions } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import * as Location from 'expo-location';
import MapView, { Marker } from 'react-native-maps';
import { FontAwesome } from '@expo/vector-icons';
import { Constants, Permissions } from 'expo';



export default function Perimiter({navigation}) {
    const [location, setLocation] = useState(null);
    const [errorMsg, setErrorMsg] = useState(null);
    const [mapRegion, setMapRegion] = useState(null);
    const [hasLocationPermissions, setHasLocationPermissions] = useState(null);
    const [locationResult, setLocationResult] = useState(null);

    const handlePress = () => {
      navigation.replace('Dashboard');
    };


    useEffect(() => {
      (async () => {
        let { status } = await Location.requestForegroundPermissionsAsync();
        if (status !== 'granted') {
          setErrorMsg('Permission to access location was denied');
          return;
        }

        let location = await Location.getCurrentPositionAsync({});
        setLocation(location);

      })();
    }, []);
  
    let text1 = 'Waiting..';
    let text2 = 'Waiting..';
    if (errorMsg) {
      text1 = errorMsg;
      text2 = errorMsg;
    } else if (location) {
      text1 = JSON.stringify(location.coords.latitude);
      text2 = JSON.stringify(location.coords.longitude);
    }

    
    setMapRegion({ locationResult: JSON.stringify(location) });

    setLocationResult({mapRegion: { latitude: location.coords.latitude, longitude: location.coords.longitude, latitudeDelta: 0.0922, longitudeDelta: 0.0421 }});

    return (
      <View style={styles.container}>
        <Text style={styles.title}>Perimiter</Text>
        {`\n`}
        <Text style={styles.boldText}>You are Here{text1}</Text>
        {`\n`}
        <Text style={styles.boldText}>You are Here{text2}</Text>
          <MapView style={{ alignSelf: 'stretch', height: 400 }}
                    region={this.state.mapRegion}
                    onRegionChange={this._handleMapRegionChange}
          />
        <TouchableOpacity style={styles.button} onPress={handlePress}>
          <Text style={styles.buttonText}>Back</Text>
        </TouchableOpacity>
      </View>
      
    );
    }

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 50,
    padding: 16,
  },
  boldText: {
    fontSize: 30,
    color: 'red',
  },
  map: {
    width: Dimensions.get('screen').width,
    height: Dimensions.get('screen').height * 0.90,
  }, 
});
