import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Dimensions } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import MapView, { PROVIDER_GOOGLE } from 'react-native-maps';
import * as Location from 'expo-location';

export default function Perimiter({navigation}) {
  const [location, setLocation] = useState(null);
  const [errorMsg, setErrorMsg] = useState(null);

  const handlePress = () => {
    navigation.replace('Dashboard');
  };

  useEffect(() => {
    (async () => {
      let { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== 'granted') {
        setErrorMsg('Permission to access location was denied');
        return;
      }
      let location = await Location.getCurrentPositionAsync({});
      setLocation(location);
      }
    )();
    }, 
    []
  );

  let text1 = 'Waiting..';
  let text2 = 'Waiting..';
  if (errorMsg) {
    text1 = errorMsg;
    text2 = errorMsg;
  } else if (location) {
    text1 = JSON.stringify(location.coords.latitude);
    text2 = JSON.stringify(location.coords.longitude);
  }

  const sendLoc =() =>{
    let latitude= location.coords.latitude
    let longitude=location.coords.longitude
    console.log("Use these variables to send current location(",latitude, ",",longitude,")")
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Perimiter</Text>
      <Text style={styles.boldText}>You are Here{text1}</Text>
      <Text style={styles.boldText}>You are Here{text2}</Text>
      <TouchableOpacity style={styles.button} onPress={handlePress}>
        <Text style={styles.buttonText}>Back</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.btLogin1} onPress={sendLoc}>
        <Text style={styles.buttonText1}>Send coordinates</Text>
      </TouchableOpacity>
      <MapView
        style={styles.map}
        provider={PROVIDER_GOOGLE}
        initialRegion={{
          latitude: 41.3995345,
          longitude: 2.1909796,
          latitudeDelta: 0.003,
          longitudeDelta: 0.003,
        }}
        mapType="standard"
      ></MapView>  
    </View>
  )
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 50,
    padding: 16,
  },
  boldText: {
    fontSize: 30,
    color: 'red',
  },
  mapStyle: {
    width: Dimensions.get('window').width,
    height: Dimensions.get('window').height,
  },
  map: {
    width: Dimensions.get('screen').width,
    height: Dimensions.get('screen').height * 0.90,
  }, 
  btLogin1: {
    borderWidth: 1.5,
    borderColor: "#3675B8",
    height: 37,
    marginHorizontal: 20,
    backgroundColor: "#3675B8",
    padding: 7,
    borderRadius: 5,
    marginTop: 12,
    marginBottom: 12,
    width: "67%",
    alignItems: "center",
  },
});
